import React from "react";

// connects a React component to a Redux store.
import { connect } from "react-redux";
import { createStream } from "../../actions";
import StreamForm from './StreamForm';

class StreamCreate extends React.Component {

  onSubmit = formValues => {
    this.props.createStream(formValues);
  };

  render() {
    return (
      // different onsubmit with a handleSubmit callback provided by redux-form
      <form
        onSubmit={this.props.handleSubmit(this.onSubmit)}
        className="ui form error"
      >
        <Field name="title" component={this.renderInput} label="Enter Title" />
        <Field
          name="description"
          component={this.renderInput}
          label="Enter Description"
        />
        <button className="ui button primary">Submit</button>
      </form>
    );
  }
}

// helper function for validating formValues
const validate = formValues => {
  const errors = {};

  if (!formValues.title) {
    // only run if the user didn't enter an title
    errors.title = "You must enter a title";
  }
  if (!formValues.description) {
    errors.description = "You must enter a description";
  }
  return errors;
};

//wrapping form so you can hook up connect and reduxForm
const formWrapped = reduxForm({
  form: "streamCreate",
  validate
})(StreamCreate);

export default connect(
  null,
  { createStream }
)(formWrapped);
