import React from "react";
import { connect } from "react-redux";
import { fetchStreams } from "../../actions/index";

class StreamList extends React.Component {
  componentDidMount() {
    // fetch streams on load
    this.props.fetchStreams();
  }

  renderList() {
    return this.props.streams.map(stream => {
      return (
        // each rendered list item will have a unique key attr using the stream's id from api link action creator
        <div className="item" key={stream.id}>
          <i className="large middle aligned icon camera" />
          <div className="content">
            {stream.title}
            <div className="description">{stream.description}</div>
          </div>
        </div>
      );
    });
  }

  render() {
    return <div><h2>
        Streams
      </h2>
      <div className="ui celled list">{this.renderList()}</div>
      </div>;
  }
}

const mapStateToProps = state => {
  // Object.values get all the values from an object and form an array containing the values from that object in order, emitting the keys
  return { streams: Object.values(state.streams) };
};

export default connect(
  mapStateToProps,
  { fetchStreams }
)(StreamList);
